import React, { useEffect, useState } from 'react';
import data from "../data.json";
import htmlIcon from "../assets/images/icon-html.svg"
import cssIcon from "../assets/images/icon-css.svg"
import jsIcon from "../assets/images/icon-js.svg"
import accessibilityIcon from "../assets/images/icon-accessibility.svg"
import { useLocation } from 'react-router-dom'

const QuizPage = () => {

    const {state} = useLocation();
    const [quiz, setQuiz] = useState();
    const [title, setTitle] = useState();
    


    useEffect(() => {
      if (state.topic === "HTML") {
        setQuiz(data.quizzes[0]);

      } else if (state.topic === "CSS") {
        setQuiz(data.quizzes[1]);
      } else if (state.topic === "Javascript") {
        setQuiz(data.quizzes[2]);
      } else if (state.topic === "Accessibility") {
        setQuiz(data.quizzes[3]);
      }
    }, [])

    // console.log(quiz.questions[0].answer)
    
  return (
    <div className="pageContainer">
      <div className="firstCurve"></div>
      <div className="secondCurve"></div>
      <div className="mainContent">
        <div className="quizIndicator"></div>
        <div className="questionContent">
          <div className="instructionText"><i>Question 6 of 10</i></div>
          <div className="questionText"></div>
        </div>
        <div className="answerOption"></div>
      </div>
    </div>
  )
}

export default QuizPage
