import React, { useEffect, useState } from 'react';
import data from "../data.json";
import "./quizPage.css";
import { useLocation } from 'react-router-dom'

const QuizPage = () => {

  const { state } = useLocation();



  const [quiz, setQuiz] = useState();
  const [icon, setIcon] = useState();
  
  // console.log(quiz.questions)
  
  useEffect(() => {
    if (state.topic === "HTML") {
      setQuiz(data.quizzes[0]);
      setIcon(htmlIcon);
    } else if (state.topic === "CSS") {
      setQuiz(data.quizzes[1]);
      setIcon(cssIcon);
    } else if (state.topic === "Javascript") {
      setQuiz(data.quizzes[2]);
      setIcon(jsIcon);
    } else if (state.topic === "Accessibility") {
      setQuiz(data.quizzes[3]);
      setIcon(accessibilityIcon);
    }
  }, [state.topic, accessibilityIcon, htmlIcon, cssIcon, jsIcon])

  return (
    <div className="pageContainer">
      <div className="firstCurve"></div>
      <div className="secondCurve"></div>
      <div className="quizContent">
        <div className="quizIndicator">
          <div className="indicatorContent">
            <div className="iconContainer">
              {icon}
            </div>
            <div className="indicatorText">
              {state.topic}
            </div>
          </div>
          <div className="swithTheme"></div>
        </div>
        <div className="questionContentContainer">
          <div className="questionContent">
            <div className="questionContentText">
              <div className="questionIndicator">
                <p className="questionIndicatorText"><i>Question 6 of 10</i></p>
              </div>
              <div className="questionTextContainer">
                <p className="questionText">
                  Which of these color contrast ratios defines the minimum WCAG 2:1 Level AA requirement for normal text?
                </p>
              </div>
            </div>
          </div>
          <div className="answerOptionsContainer">
            <div className="optionsContainer">
              <div className="optionBox">
                <p className="optionBoxText">4.5 : 1</p>
              </div>
              <div className="optionBox">
                <p className="optionBoxText">3 : 1</p>
              </div>
              <div className="optionBox">
                <p className="optionBoxText">2.5 : 1</p>
              </div>
              <div className="optionBox">
                <p className="optionBoxText">5 : 1</p>
              </div>
              <div className="submitContainer">
                <div className="submitTextContainer">
                  <p className="submitText">
                    Submit Answer
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default QuizPage
