import React, { useEffect, useState } from 'react';
import data from "../data.json";
import cssIcon from "../assets/images/icon-css.svg"
import jsIcon from "../assets/images/icon-js.svg"
import accessibilityIcon from "../assets/images/icon-accessibility.svg"
import { useLocation } from 'react-router-dom'

const QuizPage = () => {

  const { state } = useLocation();
  const [quiz, setQuiz] = useState();
  const [icon, setIcon] = useState();
  const htmlIcon = <svg className='quizHtmlIcon' xmlns="http://www.w3.org/2000/svg" width="40" height="40" fill="none" viewBox="0 0 40 40"><path fill="#FF7E35" d="M24.508 7.607a1.25 1.25 0 0 1 .634 1.65l-10 22.5a1.25 1.25 0 1 1-2.284-1.015l10-22.5a1.251 1.251 0 0 1 1.65-.635ZM10.832 13.44a1.249 1.249 0 0 1 .1 1.765L6.674 20l4.263 4.795a1.25 1.25 0 1 1-1.87 1.66l-5-5.625a1.25 1.25 0 0 1 0-1.66l5-5.625a1.25 1.25 0 0 1 1.764-.105Zm18.337 0a1.25 1.25 0 0 1 1.765.105l5 5.625a1.25 1.25 0 0 1 0 1.66l-5 5.625a1.25 1.25 0 1 1-1.87-1.66L33.327 20l-4.262-4.795a1.25 1.25 0 0 1 .105-1.765Z"/></svg>;
  const cssIcon = <svg className='' xmlns="http://www.w3.org/2000/svg" width="40" height="40" fill="none" viewBox="0 0 40 40"><path fill="#2FD887" d="M10 2.505a1.25 1.25 0 0 0-1.25 1.25V21.25a5 5 0 0 0 5 5H15v6.25a4.999 4.999 0 0 0 9.615 1.913c.25-.607.38-1.257.38-1.913v-6.25h1.255a5 5 0 0 0 5-5V3.755A1.25 1.25 0 0 0 30 2.505H10ZM28.75 17.5h-17.5V5.005h7.5V8.76a1.25 1.25 0 0 0 2.5 0V5.005h2.5v6.24a1.25 1.25 0 0 0 2.5 0v-6.24h2.5V17.5Zm-17.5 3.75V20h17.5v1.25a2.5 2.5 0 0 1-2.5 2.5h-2.505a1.25 1.25 0 0 0-1.25 1.25v7.5a2.497 2.497 0 1 1-4.995 0V25a1.25 1.25 0 0 0-1.25-1.25h-2.5a2.5 2.5 0 0 1-2.5-2.5Z"/></svg>

  useEffect(() => {
    if (state.topic === "HTML") {
      setQuiz(data.quizzes[0]);
      setIcon(htmlIcon);
    } else if (state.topic === "CSS") {
      setQuiz(data.quizzes[1]);
      setIcon(cssIcon);
    } else if (state.topic === "Javascript") {
      setQuiz(data.quizzes[2]);
      setIcon(jsIcon);
    } else if (state.topic === "Accessibility") {
      setQuiz(data.quizzes[3]);
      setIcon(accessibilityIcon);
    }
  }, [])

  // console.log(quiz.questions[0].answer)

  return (
    <div className="pageContainer">
      <div className="firstCurve"></div>
      <div className="secondCurve"></div>
      {/* <div className="mainContentContainer"> */}
      <div className="quizContent">
        <div className="quizIndicator">
          <div className="indicatorContent">
            <div className="iconContainer">
              {icon}
            </div>
            <div className="indicatorText">
              {state.topic}
            </div>
          </div>
        </div>
        <div className="questionContentContainer">
          <div className="questionContent">
            <div className="instructionText"><i>Question 6 of 10</i></div>
            <div className="questionText"></div>
          </div>
          <div className="answerOption"></div>
        </div>
      </div>
      {/* </div> */}
    </div>
  )
}

export default QuizPage
